define('text!views/account/login.html',[],function () { return '<div id="login">\r\n    <div class="container">\r\n        <div class="page-header">\r\n            <h1>Login to your account   <small>use your site credentials or any of the external providers allowed</small></h1>\r\n        </div>\r\n        <div class="row">\r\n            <div class="col-lg-6">\r\n                <form class="form-horizontal">\r\n                    <div class="page-header">\r\n                        <h4>Sign in with your credentials</h4>\r\n                    </div>\r\n                    <div class="form-group" data-bind="validationElement: username">\r\n                        <label class="control-label col-lg-4" for="username">Username</label>\r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="text" data-bind="value: username" placeholder="User name">\r\n                        </div>\r\n                    </div>\r\n                    <div class="form-group" data-bind="validationElement: password">\r\n                        <label class="control-label col-lg-4" for="email">Password</label>\r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="password" data-bind="value: password" placeholder="Password">\r\n                        </div>\r\n                    </div>\r\n                    <div class="checkbox col-lg-offset-4">\r\n                        <label class="checkbox">\r\n                            <input type="checkbox" data-bind="checked: rememberMe">Remember me\r\n                        </label>\r\n                    </div>\r\n                    <div class="col-lg-offset-4 mart2">\r\n                        <button type="submit" class="btn btn-primary" data-bind="click: login">Sign in</button>\r\n                        <button type="reset" class="btn btn-default">Reset</button>\r\n                        <hr />\r\n                        <a href="#" class="btn btn-default" data-bind="attr: { href: convertRouteToHash(\'/account/register\') }">Register</a>\r\n                        <a href="#" class="btn btn-default" data-bind="attr: { href: convertRouteToHash(\'/account/forgotpassword\') }">Forgot your password?</a>\r\n                        <hr/>\r\n                    </div>\r\n                </form>                      \r\n            </div>\r\n    \r\n            <div class="col-offset-1 col-lg-5">\r\n                <div class="page-header">\r\n                    <h4>... or you can sign in using another service</h4>\r\n                </div>\r\n                <div class="list-group" data-bind="foreach: externalLoginProviders">\r\n                    <a class="list-group-item lead" href="#" data-bind="click: login, attr: { \'data-provider\': name,  title: \'Log in using your \' + name() + \' account\'}">\r\n                        <i data-bind="css: socialIcon($data)"></i>\r\n                        <span data-bind="text: name"></span>\r\n                    </a>\r\n                </div>\r\n            </div>    \r\n        </div>\r\n    </div>\r\n</div>';});


define('text!views/account/manage.html',[],function () { return '<div id="manage">\r\n    <div class="container">\r\n        <div class="page-header">\r\n            <h1>Manage your account   <small>change passwords and manage your external accounts</small></h1>\r\n        </div>\r\n        <div class="row">\r\n            <section class="col-md-6">\r\n                <div data-bind="visible : hasLocalPassword()">\r\n                    <div data-bind="with: changePassword">\r\n                        <form role="form">\r\n                            <h2>Change your password</h2>\r\n                            <hr />\r\n                            <div class="form-group" data-bind="validationElement: oldPassword">\r\n                                <label for="ChangePasswordOldPassword" class="control-label">Old password</label>\r\n                                <input type="password" id="ChangePasswordOldPassword" class="form-control" data-bind="value: oldPassword" />                                \r\n                            </div>\r\n                            <div class="form-group" data-bind="validationElement: newPassword">\r\n                                <label for="ChangePasswordNewPassword" class="control-label">New password</label>\r\n                                <input type="password" id="ChangePasswordNewPassword" class="form-control" data-bind="value: newPassword" />\r\n                            </div>\r\n                            <div class="form-group" data-bind="validationElement: confirmPassword">\r\n                                <label for="ChangePasswordConfirmPassword" class="control-label">Confirm new password</label>\r\n                                <input type="password" id="ChangePasswordConfirmPassword" class="form-control" data-bind="value: confirmPassword" />\r\n                            </div>\r\n                            <div class="form-group">\r\n                                <button type="submit" class="btn btn-primary" data-bind="click: change, disable: changing">Cambiar contraseña</button>\r\n                                <button type="reset" class="btn btn-default">Reset</button>\r\n                            </div>\r\n                        </form>\r\n                    </div>\r\n                </div>\r\n                <div data-bind="visible : !hasLocalPassword()">\r\n                    <div data-bind="with: setPassword">\r\n                        <p class="lead">You don´t have any local password for this site. Add a local account by entering a password in this form and you won´t need any external login anymore</p>\r\n                        <form class="form-horizontal" role="form">\r\n                            <h2>Create local account</h2>\r\n                            <hr />\r\n                            <div class="form-group" data-bind="validationElement: newPassword">\r\n                                <label for="SetPasswordNewPassword" class="control-label">New password</label>\r\n                                <input type="password" id="SetPasswordNewPassword" class="form-control" data-bind="value: newPassword" />                                \r\n                            </div>\r\n                            <div class="form-group" data-bind="validationElement: confirmPassword">\r\n                                <label for="SetPasswordConfirmPassword" class="control-label">Confirm new password</label>\r\n                                <input type="password" id="SetPasswordConfirmPassword" class="form-control" data-bind="value: confirmPassword" />                                \r\n                            </div>\r\n                            <div class="form-group">\r\n                               <button type="submit" class="btn btn-primary" data-bind="click: set, disable: setting">Create local account</button>                               \r\n                                <button type="reset" class="btn btn-default">Reset</button>\r\n                            </div>\r\n                        </form>\r\n                    </div>\r\n                </div>\r\n                <div>\r\n                    <h2>Danger Area</h2>\r\n                    <hr />\r\n                    <p>Click the following button if you want to delete your account. Be careful because this action is permanent.</p>\r\n                    <a href="#" class="btn btn-danger" data-bind="click:deleteAccount">Delete the account</a>\r\n                </div>\r\n                <hr />\r\n          </section>\r\n            <section class="col-md-5 col-md-offset-1">\r\n                <div data-bind="if: userLogins().length > 0">\r\n                    <h2>Registered external accounts</h2>\r\n                    <table class="table">\r\n                        <tbody data-bind="foreach: userLogins">\r\n                            <tr>\r\n                                <td class="lead" data-bind="text: loginProvider"></td>\r\n                                <td>\r\n                                    <div data-bind="if: $parent.canRemoveLogin">\r\n                                        <form>\r\n                                            <div>\r\n                                                <button type="submit" class="btn btn-danger pull-right" data-bind="attr: { title: \'Remove this \' + loginProvider() + \' credential from your account\' }, click: remove, disable: removing"><i class="fa fa-trash-o"></i>   Remove</button>\r\n                                            </div>\r\n                                        </form>\r\n                                    </div>\r\n                                    <span data-bind="if: !$parent.canRemoveLogin()">&nbsp;</span>\r\n                                </td>\r\n                            </tr>\r\n                        </tbody>\r\n                    </table>\r\n                </div>\r\n                <h2>Add a new external account</h2>\r\n                <hr />\r\n                <div data-bind="visible: !hasExternalLogin()">\r\n                    <p class="lead">\r\n                        There are no external authentication services configured. See <a href="http://go.microsoft.com/fwlink/?LinkId=313242">this article</a>\r\n                        for details on setting up this ASP.NET application to support logging in via external services.\r\n                    </p>\r\n                </div>\r\n                <div data-bind="visible: hasExternalLogin">\r\n                    <div class="list-group" data-bind="foreach: externalLoginProviders">\r\n                        <a href="#" class="list-group-item lead" data-bind="click: login, attr: { \'data-provider\': name,  title: \'Log in using your \' + name() + \' account\'}">\r\n                            <i data-bind="css: socialIcon($data)"></i>\r\n                            <span data-bind="text: name"></span>\r\n                        </a>\r\n                    </div>\r\n                </div>                \r\n            </section>\r\n        </div>\r\n    </div>\r\n</div>';});


define('text!views/account/register.html',[],function () { return '<div id="register">\r\n    <div class="container">\r\n        <div class="page-header">\r\n            <h1 class="text-center">Register now   <small>create a new account in this site</small></h1>\r\n        </div>\r\n        <div class="row">\r\n            <div class="col-lg-offset-3 col-lg-6">        \r\n                <form data-bind="submit : register" class="form-horizontal">\r\n                    <div class="form-group" data-bind="validationElement: username">\r\n                         <label class="control-label col-lg-4" for="username">Username</label>                        \r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="text"  data-bind="value: username" placeholder="User name">      \r\n                        </div>\r\n                    </div>\r\n                    <div class="form-group" data-bind="validationElement: email">\r\n                         <label class="control-label col-lg-4" for="username">Email</label>                     \r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="text"  data-bind="value: email" placeholder="Email"> \r\n                        </div>\r\n                    </div>                   \r\n                    <div class="form-group" data-bind="validationElement: password">\r\n                         <label class="control-label col-lg-4" for="email">Password</label>\r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="password"  data-bind="value: password" placeholder="Password">  \r\n                        </div>\r\n                    </div>  \r\n                    <div class="form-group" data-bind="validationElement: confirmpassword">\r\n                         <label class="control-label col-lg-4" for="email">Confirm Password</label>\r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="password"  data-bind="value: confirmpassword" placeholder="Confirm Password">  \r\n                        </div>\r\n                    </div>  \r\n                    <div class="col-lg-offset-4">\r\n                        <button type="submit" class="btn btn-primary">Register</button>\r\n                        <button type="reset" class="btn btn-default">Reset</button>\r\n                    </div>    \r\n                </form>                      \r\n            </div>          \r\n        </div>\r\n        <hr/>\r\n    </div>\r\n </div>';});


define('text!views/account/registrationcomplete.html',[],function () { return '<div id="registrationcomplete">\r\n    <div class="container">\r\n        <div class="page-header">\r\n            <h1>Your account was succesfully confirmed!!</h1>\r\n        </div>\r\n        <h3><a href="#" data-bind="attr: { href: router.convertRouteToHash(\'/account/login\') }" class="lead">Sign in</a> with your credentials and start using your account ... or you can:</h3>\r\n        <ul class="list-unstyled mart2">\r\n            <li><a href="#" data-bind="attr: { href: router.convertRouteToHash(\'/\') }" class="lead">Go to the home page</a></li>\r\n            <li><a href="#" data-bind="attr: { href: router.convertRouteToHash(\'/home/articles\') }" class="lead">Read articles in this site</a></li>\r\n            <li><a href="#" data-bind="attr: { href: router.convertRouteToHash(\'/user/dashboard\') }" class="lead">Go to your dashboard</a></li>\r\n            <li><a href="#" data-bind="attr: { href: router.convertRouteToHash(\'/account/manage\') }" class="lead">Manage your account</a></li>\r\n        </ul>\r\n    </div>\r\n</div>';});


define('text!views/account/resetpassword.html',[],function () { return '<div id="resetpassword">\r\n    <div class="container">\r\n        <div class="page-header">\r\n            <h1 class="text-center">Enter your mail and password   <small>... and you will reset your password to a new one</small></h1>\r\n        </div>\r\n        <div class="row">\r\n            <div class="col-lg-offset-3 col-lg-6">\r\n                <form data-bind="submit : resetPassword" class="form-horizontal">\r\n                    <div class="form-group" data-bind="validationElement: email">\r\n                        <label class="control-label col-lg-4" for="username">Email</label>\r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="text" data-bind="value: email" placeholder="Email">\r\n                        </div>\r\n                    </div>\r\n                    <div class="form-group" data-bind="validationElement: password">\r\n                        <label class="control-label col-lg-4" for="email">Password</label>\r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="password" data-bind="value: password" placeholder="Password">\r\n                        </div>\r\n                    </div>\r\n                    <div class="form-group" data-bind="validationElement: confirmpassword">\r\n                        <label class="control-label col-lg-4" for="email">Confirm Password</label>\r\n                        <div class="col-lg-8">\r\n                            <input class="form-control" type="password" data-bind="value: confirmpassword" placeholder="Confirm Password">\r\n                        </div>\r\n                    </div>\r\n                    <div class="col-lg-offset-4">\r\n                        <button type="submit" class="btn btn-primary">Reset the password</button>\r\n                        <button type="reset" class="btn btn-default">Clean fields</button>\r\n                    </div>\r\n                </form>\r\n            </div>\r\n        </div>\r\n        <hr />\r\n    </div>\r\n</div>';});


